import * as React from 'react'
import styled from 'styled-components'

import { CubeSpinner } from '../Spinner/Spinner'
import css from './Loading.module.css'

const Wrapper = styled.div<{ fullscreen: boolean, transparent: boolean, fadeout?: boolean, slideout?: boolean }>`
    display: flex;
    justify-content: center;
    align-items: center;
    position: fixed;
    top: 0;
    left: 0;
    z-index: 20;
    opacity: 1;
    visibility: visible;
    transition:  visibility 0s .5s, top .5s, opacity .5s;
    width: ${props => props.fullscreen ? '100vw' : 'auto' };
    height: ${props => props.fullscreen ? '100vh' : 'auto' };
    background: ${props => props.transparent ? 'transparent' : 'snow' };
`

type TLoadingProps = {
    show: boolean
    transparent?: boolean
    fadeout?: boolean
    slideout?: boolean
    fullscreen?: boolean
}

const Loading: React.FunctionComponent<TLoadingProps> = (props): JSX.Element => {
    const { show, transparent, fadeout, slideout, fullscreen }: TLoadingProps = props

    const styles = [
        css.Loading,
        transparent && css.transparent,
        fullscreen ? css.fullscreen : css.notfull,
        (!show && fadeout) && css.fadeout,
        (!show && slideout) && css.slideout,
    ].join(' ')

    return (
        <div className={styles}>
            <CubeSpinner />
        </div>
    )
}

export default Loading
