export enum formTypes {
    ADD = 'add',
    EDIT = 'edit',
}

export interface IForm {
    [title: string]: any
    description: any
    language: any
    link: any
    github: any
    video_src: any
    gif_src: any
    desktop: any
    mobile: any
}

export const initialForm: IForm = {
    title: {
        id: 'title',
        elemType: 'input',
        type: 'text',
        placeholder: 'title',
        require: true,
        value: '',
    },
    description: {
        id: 'description',
        elemType: 'textarea',
        type: 'text',
        placeholder: 'description',
        require: true,
        value: '',
    },
    language: {
        id: 'languages',
        elemType: 'select',
        options: {
            React: {
                selected: false,
            },
            TypeScript: {
                selected: false,
            },
            JavaScript: {
                selected: false,
            },
            Cpp: {
                selected: false
            },
            NextJS: {
                selected: false
            }
        },
        require: true,
        multiple: true
    },
    link: {
        id: 'link',
        elemType: 'input',
        type: 'text',
        placeholder: 'link to app',
        require: true,
        value: '',
    },
    github: {
        id: 'github',
        elemType: 'input',
        type: 'text',
        placeholder: 'github link',
        require: true,
        value: '',
    },
    video_src: {
        id: 'video_src',
        elemType: 'input',  
        type: 'text',
        placeholder: 'video link',
        require: true,
        value: '',
    },
    gif_src: {
        id: 'gif_src',
        elemType: 'input',
        type: 'text',
        placeholder: 'gif link',
        require: true,
        value: '',
    },
    desktop: {
        id: 'desktop',
        elemType: 'select',
        options : {
            Yes: {
                selected: false,
            },
            No: {
                selected: false
            },
        },        
        require: true,
    },
    mobile: {   
        id: 'mobile',
        elemType: 'select',
        options : {
            Yes: {
                selected: false,
            },
            No: {
                selected: false
            },
        }, 
        require: true,
    }
}
